import { StyleSheet, ImageBackground, TextInput } from 'react-native';


const dynamicStyles = (appStyles, colorScheme) => {
  return StyleSheet.create({
    title: {
      fontSize: 30,
      fontWeight: 'bold',
      textAlign: 'center',
      paddingBottom: 3,
      color: 'white',
    },
    text: {
      fontSize: 10,
      textAlign: 'center',
      color: 'white',
      paddingLeft: 10,
      paddingRight: 10,
    },
    image: {
      width: 200,
      height: 300,
      marginBottom: 60,
      paddingTop: 20,
      borderRadius: 20
      //tintColor: 'white',
    },
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      height: '100%',
      //backgroundColor: appStyles.colorSet[colorScheme].mainThemeForegroundColor,
      backgroundColor: '#CB38B7'
      },
    button: {
      fontSize: 18,
      color: 'white',
      marginTop: 10,
    },
    backgroundImage: {
      flex: 1,
      resizeMode: 'cover',
      justifyContent: 'center',
    },
    textInput: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    }
  });
};
export default dynamicStyles;
